mapper.insert:
INSERT /* com.planty.jsp.user.model.dao.UserDAO#insertUser() */
  INTO MEMBER 
(
  ID
, AUTH_NO
, PWD
, ADDRESS
, ADDR_DETAIL
, ZIPCODE
, PHONE
, EMAIL
, TEXT_YN
, ENROLL_DATE
, NAME
)
VALUES 
(
  #{ id }
, 1
, #{ pwd }
, #{ address }
, #{ addrDetail }
, #{ zipcode }
, #{ phone }
, #{ email }
, #{ textYn }
, SYSDATE
, #{ name }
)

mapper.select:
 /* com.planty.jsp.user.model.dao.UserDAO#UserIdCheck() */
SELECT
       COUNT(*)
  FROM MEMBER
 WHERE ID = #{ id }

mapper.update:
UPDATE /* com.planty.jsp.user.model.dao.UserDAO#updateUser() */
       MEMBER
   SET ADDRESS = #{ address }
     , ADDR_DETAIL = #{ addrDetail }
     , ZIPCODE = #{ zipcode }
     , PHONE = #{ phone }
     , EMAIL = #{ email }
     , TEXT_YN = #{ textYn }
 WHERE ID = #{ id }

UPDATE /* com.planty.jsp.user.model.dao.UserDAO#updateUserPassword() */
       MEMBER
   SET PWD = #{ pwd }
 WHERE ID = #{ id }

mapper.delete:
/* com.planty.jsp.user.model.dao.UserDAO#deleteUser() */		
	DELETE FROM MEMBER
	 WHERE ID = #{ id }

mapper.select:
SELECT 
	   ID
 FROM MEMBER 
 WHERE NAME = #{name} 
 AND EMAIL = #{email}

SELECT 
		*
 FROM MEMBER 
 WHERE ID = #{ id } 
 AND EMAIL = #{email}

	AND REG_NO = #{ regNo }
