mapper.insert:
INSERT /* com.greedy.jsp.board.model.dao.BoardDAO#insertBoard() */
  INTO TBL_BOARD 
(
  BOARD_NO
, BOARD_TYPE
, CATEGORY_CODE
, BOARD_TITLE
, BOARD_BODY
, BOARD_WRITER_MEMBER_NO
)
VALUES
(
  SEQ_BOARD_NO.NEXTVAL
, 1
, #{ categoryCode }
, #{ title }
, #{ body }
, #{ writerMemberNo }
)

INSERT /* com.greedy.jsp.board.model.dao.BoardDAO#insertAttachment() */
  INTO TBL_ATTACHMENT 
(
  ATTACHMENT_NO
, REF_BOARD_NO
, ORIGINAL_NAME
, SAVED_NAME
, SAVE_PATH
, FILE_TYPE
, THUMBNAIL_PATH
)
VALUES 
(
  SEQ_ATTACHMENT_NO.NEXTVAL
, #{ refBoardNo }
, #{ originalName }
, #{ savedName }
, #{ savePath }
, #{ fileType }
, #{ thumbnailPath }
)

mapper.update:
UPDATE /* com.greedy.jsp.board.model.dao.BoardDAO#incrementBoardCount() */
       TBL_BOARD A
   SET A.BOARD_COUNT = (SELECT B.BOARD_COUNT
                          FROM TBL_BOARD B
                         WHERE B.BOARD_NO = #{ no }
                       ) + 1
 WHERE A.BOARD_NO = #{ no }
