mapper.update:
UPDATE /* com.greedy.jsp.notice.model.dao.NoticeDAO#incrementNoticeCount() */
       NOTICE A
   SET A.NOTICE_COUNT = (SELECT B.NOTICE_COUNT
                           FROM NOTICE B
                          WHERE B.NOTICE_NO = #{ no }
                        ) + 1
 WHERE A.NOTICE_NO = #{ no }

UPDATE /* com.greedy.jsp.notice.model.dao.NoticeDAO#updateNotice() */
       NOTICE
   SET TITLE = #{ title }
     , BODY = #{ content }
 WHERE NOTICE_NO = #{ no }
